import streamlit as st
import pandas as pd
import plotly.express as px
import matplotlib.pyplot as plt
import numpy as np
import os
import csv
import dummy

write_path = r'/Users/L010166/Library/CloudStorage/OneDrive-EliLillyandCompany/md_ids/Challenges/jarvis-2023/git_repo/cric_pred/streamlit-dynami-app/data'
match_file = '1359475_results.csv'

def create_bar_graph(team1, team2):
    '''
    Row_No,Team1,match_id,is_batting_team,Team2,innings_over,innings_score,innings_wickets,score_target,total_runs,player_dismissed,predictions,wickets_in_over,required_run_rate,predicted_team1,predicted_team2
    '''
    
    path = os.path.join(write_path, match_file)
    val_df = pd.read_csv(path)

    out_df = pd.DataFrame({'Team1':val_df.Team1.values})
    out_df['batting_team'] = val_df.is_batting_team.values
    out_df['Team1'] = val_df.Team1.values
    out_df['Team2'] = val_df.Team2.values
    out_df['innings_over'] = val_df.innings_over.values
    out_df['innings_score'] = val_df.innings_score.values
    out_df['innings_wickets'] = val_df.innings_wickets.values
    out_df['score_target'] = val_df.score_target.values
    out_df['total_runs'] = val_df.total_runs.values
    out_df['predictions_team1'] = val_df.predicted_team1.values
    out_df['predictions_team2'] = val_df.predicted_team2.values
    out_df['wickets_in_over'] = val_df.player_dismissed.values
    out_df['required_run_rate'] = val_df.required_run_rate.values

    print(out_df.head())
    
    fig, ax1 = plt.subplots(figsize=(12,6))
    ax2 = ax1.twinx()

    # values for x axis
    labels = np.array(out_df['innings_over'])
    ind = np.arange(len(labels))
    width = 0.7

    # create bar plots
    rects = ax1.bar(ind, np.array(out_df['innings_score']), width=width, color=['yellow']*20 + ['green']*20)
    ax1.set_xticks(ind+((width)/2.))
    ax1.set_xticklabels(labels, rotation='vertical')

    # set x, y and title labels
    ax1.set_ylabel("Innings score")
    ax1.set_xlabel("Innings and over")
    ax1.set_title(f"Win percentage prediction over by over {team1} - {team2}")

    # create probabiliy score line
    ax2.plot(ind, np.array(out_df['predictions_team1']), color='b', marker='o', label=f"{team1}")
    ax2.plot(ind, np.array(out_df['predictions_team2']), color='m', marker='o', label=f"{team2}")

    ax2.plot(ind, np.array([0.5]*len(out_df['predictions_team1'])), color='red', marker='o', label=f"Average")
    
    ax2.legend()

    ax2.set_ylim([0,1])
    ax2.grid(False)

    plt.savefig('bar-graph', dpi=400)
    st.image('bar-graph.png')

def create_slider(team1, team2, pred1, pred2):
    
    st.slider('{}'.format(team1), 0.0, 100.0, (float(float(pred1)*100)), key="team1")
    st.slider('{}'.format(team2), 0.0, 100.0, (float(float(pred2)*100)), key="team2")

def create_probability_bar(team1, team2, pred1, pred2):
    
    df = pd.DataFrame({'Team': [team1, team2], 'Winning Probability': [float(float(pred1)*100), float(float(pred2)*100)]})
    df2 = df.set_index("Team").unstack().to_frame().reset_index()
    fig = px.bar(df2, x=0, y='level_0', color='Team', orientation='h',color_discrete_sequence=px.colors.qualitative.Dark2)
    fig.update_yaxes(showticklabels=False)
    fig.update_xaxes(title_text='Winning Probability')
    fig.update_yaxes(title_text='')
    team2_logo = 'data:image/jpeg;base64,'
    team1_logo = 'data:image/jpeg;base64,'

    fig.update_layout(
        images=[
            dict(
                source=team1_logo,
                xref="paper", yref="paper",
                x=0.1, y=1.05,
                sizex=0.5, sizey=0.5,
                xanchor="right", yanchor="bottom"
            ),
            dict(
                source=team2_logo,
                xref="paper", yref="paper",
                x=0.95, y=1.05,
                sizex=0.5, sizey=0.5,
                xanchor="right", yanchor="bottom"
            )
        ])
    
    fig.update_layout(
        height=200,
        width=800,
        title_text="",
        title_x=0.1,
        font=dict(
            family="Courier New, monospace",
            size=18,
            color="#7f7f7f"
        )
    )

    fig.update_xaxes(
        tickmode = 'array',
        tickvals = [i for i in range(0,105,5)],
        ticktext = [i for i in range(0,105,5)]
    )

    st.plotly_chart(fig)

def fecth_data():

    '''
    Column Header
       0 , 1 Team1, 2 match_id, 3 is_batting_team,
       4 Team2, 5 innings_over, 6 innings_score,
       7 innings_wickets, 8 score_target, 9 total_runs,
       10 player_dismissed, 11 predictions, 12 wickets_in_over,
       13 required_run_rate, 14 predicted_team1, 15 predicted_team2
    '''
    
    # read live data
    read_file = os.path.join(write_path, match_file)
    def read_row():
        with open(read_file, 'r') as r_file:
            csvreader = csv.reader(r_file)
            next(csvreader, None)
            last_record = r_file.readlines()[-1].split(",")
            yield last_record

    pred1 = 0.0
    pred2 = 0.0
    team1 = ''
    team2 = ''
    for row in read_row():
        team1 = row[1]
        team2 = row[4]
        pred1 = row[14]
        pred2 = row[15]
    
    create_slider(team1, team2, pred1, pred2)
    create_probability_bar(team1, team2, pred1, pred2)
    create_bar_graph(team1, team2)
    
    

def main():

    # front end elements of the web page
    html_temp = """
    <div style ="background-color:tomato;padding:13px">
    <h1 style ="color:white;text-align:center;">IPL Winner Prediction ML App</h1>
    </div>
    """

    # display the front end aspect
    st.markdown(html_temp, unsafe_allow_html = True)

    fecth_data()
    


if __name__=='__main__':

    main()